import React , {Component} from 'react';

class App extends Component {

    constructor(){
        super()
        this.state = {
            number:0
        }
        
        this.clicpluskHandler=this.clicpluskHandler.bind(this);
        this.clickmoinHandler=this.clickmoinHandler.bind(this);
    }

    clicpluskHandler(){
        this.setState(function(prset){
            return{
                number:prset.number +1
            }
        })
    }

    clickmoinHandler(){
        this.setState(function(prset){
            return{
                number:prset.number -1
            }
        })
    }

    clickHandler(){
        this.setState({
            number:0
        })
    }
    

    render(){
        return(
            <div className='container text-center mt-5  '>
                <h1 className='mb-5'>{this.state.number}</h1>
                <button className='btn btn-success p-3 rounded-pill me-3' onClick={this.clicpluskHandler}>Plus</button> 

                <button className='btn btn-primary p-3 rounded-pill  me-3 ' onClick={this.clickHandler}>Zero</button>

                <button className='btn btn-danger p-3 rounded-pill ' onClick={this.clickmoinHandler}>moin</button>
            </div>
           
        )
    }
}

export default App;
